				   	select concept_id from concept_name where name = 'HTS Program Outcomes'
					and concept_name_type = 'FULLY_SPECIFIED' and locale = 'en' and voided = 0
				   ),
				   (
				   select user_id from users where username='superman'
				   ),
				   now(),'HTS Program','HTS Program',uuid()
   			   );

set @concept_id = 0;
set @member1_concept_id = 0;

select concept_id into @concept_id from concept_name where name = 'HTS Program Outcomes' and concept_name_type = 'FULLY_SPECIFIED' and locale = 'en' and voided = 0;

select concept_id into @member1_concept_id from concept_name where name = "HIV positive" and concept_name_type = 'FULLY_SPECIFIED' and locale = 'en' and voided = 0;

call add_concept_set_members (@concept_id, @member1_concept_id, 1);

        insert into entity_mapping(uuid, entity_mapping_type_id, entity1_uuid, entity2_uuid, date_created) 
