SELECT concept_id INTO @concept_id FROM concept_name WHERE name = 'All Observation Templates' AND concept_name_type="FULLY_SPECIFIED" and voided = 0;
SELECT max(sort_weight) into @weight from concept_set where concept_id = (select concept_id from concept_name where name = 'All Observation Templates' and concept_name_type = 'FULLY_SPECIFIED' and voided = 0);

SELECT concept_id INTO @concept_id43 FROM concept_name WHERE name = 'VIAC Form Template' AND concept_name_type="FULLY_SPECIFIED" and voided = 0;
INSERT INTO concept_set(concept_id,concept_set,sort_weight,creator,date_created,uuid) VALUES (@concept_id43,@concept_id,@weight,1,now(),uuid());
